 var nodemailer = require("nodemailer");

exports.sendemail = function(req, res, to, next){
  // create reusable transport method (opens pool of SMTP connections)
  var smtpTransport = nodemailer.createTransport("SMTP",{
    service: "Gmail",
    auth: {
      user: "edWAR6@gmail.com",
      pass: "letmeing"
    }
  });

  // setup e-mail data with unicode symbols
  var mailOptions = {
    from: '"'+ req.body.contact.name +'" <'+ req.body.contact.email +'>', // sender address
    to: to, // list of receivers
    subject: req.body.contact.subject, // Subject line
    text: req.body.contact.message, // plaintext body
    html: ''+ req.body.contact.message +'' // html body
  }

  // send mail with defined transport object
  smtpTransport.sendMail(mailOptions, function(error, response){
    if(error){
      console.log(error);
      next(error);
    }else{
      console.log("Message sent: " + response.message);
      next();
    };

    // if you don't want to use this transport object anymore, uncomment following line
    //smtpTransport.close(); // shut down the connection pool, no more messages
  });
};