var passport = require('passport'),
  LocalStrategy = require('passport-local').Strategy,
  FacebookStrategy = require('passport-facebook').Strategy,
  TwitterStrategy = require('passport-twitter').Strategy,
  GoogleStrategy = require('passport-google').Strategy;
var User = require('./../models/user');

passport.use(new LocalStrategy({
    usernameField: 'email',
    passwordField: 'password'
  },
  function(email, password, done) {
    User.find('lite', { email: email }, function(err, users) {
      if (err) { return done(err); }
      if (!users[0]) {
        return done(null, false, { message: 'Incorrect email.' });
      }
      if (!(users[0].password === password)) {
        return done(null, false, { message: 'Incorrect password.' });
      }
      return done(null, users[0]);
    });
  }
));

passport.use(new FacebookStrategy({
    clientID: '160105650823665',
    clientSecret: '698b08a8cd3b055906287bcd0db6fa7a',
    callbackURL: "http://www.gamepointscr.com/login/facebook/return"
  },
  function(accessToken, refreshToken, profile, done) {
    User.findOrCreate(profile, function(err, user) {
      if (err) { return done(err); }
      done(null, user);
    });
  }
));

passport.use(new TwitterStrategy({
    consumerKey: 'KsQqCA5ZawSWkSXpHGr8Q',
    consumerSecret: 'vnF838LReZikgE70XQABy7ZXLnRy8FA2cO6vv2Wqu1c',
    callbackURL: "http://www.gamepointscr.com/login/twitter/return"
  },
  function(token, tokenSecret, profile, done) {
    User.findOrCreate(profile, function(err, user) {
      if (err) { return done(err); }
      done(null, user);
    });
  }
));

passport.use(new GoogleStrategy({
    returnURL: 'http://www.gamepointscr.com/login/google/return',
    realm: 'http://www.gamepointscr.com/'
  },
  function(identifier, profile, done) {
    User.findOrCreate(profile, function(err, user) {
      done(err, user);
    });
  }
));

passport.serializeUser(function(user, done) {
  done(null, user.id);
});

passport.deserializeUser(function(id, done) {
  User.find('lite', { id: id }, function (err, users) {
    done(err, users[0]);
  });
});

exports.passport = passport;